import React, { useContext } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import classNames from 'classnames';
import { faMoon, faSun } from '@fortawesome/free-solid-svg-icons';
import { Context } from '../../../common/containers/App';

import '../styles/_app.scss';

const CONSTANTS = {
  DARK: 'dark',
  LIGHT: 'light',
};

const styles = {
  light: {
    icon: faMoon,
  },
  dark: {
    icon: faSun,
    color: '#FFA500',
  },
};
const background = {
  dark: { next: CONSTANTS.LIGHT },
  light: { next: CONSTANTS.DARK },
};

function App() {
  const { theme, onChangeTheme } = useContext(Context);
  return (
    <div
      className={classNames({
        'app dark-mode': theme === CONSTANTS.DARK,
        app: theme === CONSTANTS.LIGHT,
      })}
    >
      <div className="level">
        <div>
          <h1 className="title">Dark Mode Challenge</h1>
        </div>

        {/* --The button that should toggle dark mode-- */}
        <button
          className="app__dark-mode-btn icon level-right"
          onClick={() => onChangeTheme(background[theme].next)}
        >
          <FontAwesomeIcon {...styles[theme]} />
        </button>
      </div>

      <div className="columns">
        <div className="column">
          <p>
            Lollipop powder powder. Cotton candy caramels chupa chups halvah
            muffin caramels apple pie topping cake. Topping chocolate bar pastry
            chocolate cake. Cupcake tart jujubes dragée jelly-o icing sugar
            plum. Chocolate bar lollipop candy canes. Biscuit croissant apple
            pie pudding caramels wafer tart tootsie roll macaroon. Croissant
            tiramisu chocolate bar carrot cake lemon drops halvah.
          </p>
        </div>
        <div className="column">
          <p>
            Marshmallow tiramisu liquorice bear claw chocolate bar bear claw
            tart. Muffin chupa chups pie. Brownie apple pie topping lemon drops
            marzipan toffee. Pudding macaroon icing ice cream bonbon cake tart.
            Pudding sugar plum chocolate cake cake biscuit pastry pastry
            chocolate bar tart. Lemon drops dessert gummies icing.
          </p>
        </div>
      </div>

      <div className="field">
        <div className="control">
          <input className="input" type="text" placeholder="Name" />
        </div>
      </div>

      <div className="field">
        <div className="control">
          <input className="input" type="text" placeholder="Email" />
        </div>
      </div>

      <section className="section">
        <div className="buttons level-right">
          <a className="button is-primary">Save</a>
          <a className="button is-link">Submit</a>
        </div>
      </section>
    </div>
  );
}

export default App;
